//堆的下沉操作
void heapSink(vector &arr, int pos, int len){
    int parent = pos;
    int child = parent * 2 + 1;
    int num = arr[pos];
    while(child  arr[child]){
            child++;
        }
        if(num > arr[child]){
            break;
        }
        arr[parent] = arr[child];
        parent = child;
        child = parent * 2 + 1;
    }
    arr[parent] = num;
}

void heapSort(vector &arr){
