}

void drawScreen(){
    SDL_LockSurface(surface); 
    SDL_FillRect(surface,NULL,black);
    Uint32* pixel = (Uint32*)surface->pixels;
    for(int x =0;xw /2;x++){
        for(int y=0;yh/2;y++){
            drawPixel(x,y,red);
        }
    }
    flipY();
    SDL_UnlockSurface(surface);
    SDL_UpdateWindowSurface(window);
}


int main(int argv, char** args)
{
